INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U24_B1_I4', 'ingles-b1-U24', 4, 'multiple_choice', 'Choose the correct answer for each question about conditionals.', '{"prompt_es":"Choose the correct answer for each question about conditionals.","mastery_tag":"grammar:b1-conditionals","explanation":"''Would have'' se utiliza para situaciones pasadas que no sucedieron.","stimulus_en":"If he had known about the meeting, he ___ come.","options":[{"id":"o1","text":"will"},{"id":"o2","text":"would have"},{"id":"o3","text":"would"},{"id":"o4","text":"might have"}],"correct_answer":"o2","complexity":2,"concept_tags":["grammar","conditionals","unit"]}')
