VALUES ('B1_U24_B1_I5', 'ingles-b1-U24', 5, 'multiple_choice', 'Choose the correct answer for each question about conditionals.', '{"prompt_es":"Choose the correct answer for each question about conditionals.","mastery_tag":"grammar:b1-conditionals","explanation":"''Will'' indica una acción futura que ocurrirá si se cumple una condición.","stimulus_en":"If you call me, I ___ help you with your homework.","options":[{"id":"o1","text":"might"},{"id":"o2","text":"will"},{"id":"o3","text":"would"},{"id":"o4","text":"should"}],"correct_answer":"o2","complexity":2,"concept_tags":["grammar","conditionals","unit"]}')
