VALUES ('B1_U24_B2_I1', 'ingles-b1-U24', 6, 'multiple_choice', 'Choose the correct option to complete each sentence about relative clauses.', '{"prompt_es":"Choose the correct option to complete each sentence about relative clauses.","mastery_tag":"grammar:b1-relative-clauses","explanation":"En este caso, ''that'' se usa para definir el libro específico que me prestaste.","stimulus_en":"The book ___ you lent me is fantastic.","options":[{"id":"o1","text":"who"},{"id":"o2","text":"which"},{"id":"o3","text":"that"},{"id":"o4","text":"when"}],"correct_answer":"o3","complexity":2,"concept_tags":["grammar","relative","clauses","unit"]}')
