INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U24_B2_I2', 'ingles-b1-U24', 7, 'multiple_choice', 'Choose the correct option to complete each sentence about relative clauses.', '{"prompt_es":"Choose the correct option to complete each sentence about relative clauses.","mastery_tag":"grammar:b1-relative-clauses","explanation":"Aquí, ''who'' se utiliza para añadir información sobre mi hermana.","stimulus_en":"My sister, ___ is a doctor, lives in London.","options":[{"id":"o1","text":"who"},{"id":"o2","text":"which"},{"id":"o3","text":"where"},{"id":"o4","text":"when"}],"correct_answer":"o1","complexity":2,"concept_tags":["grammar","relative","clauses","unit"]}')
