VALUES ('B1_U24_B2_I3', 'ingles-b1-U24', 8, 'multiple_choice', 'Choose the correct option to complete each sentence about relative clauses.', '{"prompt_es":"Choose the correct option to complete each sentence about relative clauses.","mastery_tag":"grammar:b1-relative-clauses","explanation":"''Where'' se usa para indicar el lugar donde sucedió algo.","stimulus_en":"The restaurant ___ we dined is famous for its pizza.","options":[{"id":"o1","text":"where"},{"id":"o2","text":"which"},{"id":"o3","text":"that"},{"id":"o4","text":"who"}],"correct_answer":"o1","complexity":2,"concept_tags":["grammar","relative","clauses","unit"]}')
