INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U24_B2_I4', 'ingles-b1-U24', 9, 'multiple_choice', 'Choose the correct option to complete each sentence about relative clauses.', '{"prompt_es":"Choose the correct option to complete each sentence about relative clauses.","mastery_tag":"grammar:b1-relative-clauses","explanation":"''When'' se utiliza para definir un tiempo específico en una oración.","stimulus_en":"I will never forget the day ___ we met.","options":[{"id":"o1","text":"when"},{"id":"o2","text":"who"},{"id":"o3","text":"that"},{"id":"o4","text":"where"}],"correct_answer":"o1","complexity":2,"concept_tags":["grammar","relative","clauses","unit"]}')
