INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U24_B2_I5', 'ingles-b1-U24', 10, 'multiple_choice', 'Choose the correct option to complete each sentence about relative clauses.', '{"prompt_es":"Choose the correct option to complete each sentence about relative clauses.","mastery_tag":"grammar:b1-relative-clauses","explanation":"''That'' se usa aquí para especificar las películas que quiero ver.","stimulus_en":"There are many movies ___ I want to watch.","options":[{"id":"o1","text":"which"},{"id":"o2","text":"that"},{"id":"o3","text":"who"},{"id":"o4","text":"when"}],"correct_answer":"o2","complexity":2,"concept_tags":["grammar","relative","clauses","unit"]}')
