INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U24_B3_I1', 'ingles-b1-U24', 11, 'key_word_transformation', 'Reescribe la frase usando la palabra clave ''which'':', '{"prompt_es":"Reescribe la frase usando la palabra clave ''which'':","mastery_tag":"grammar:b1-relative-clauses","explanation":"En esta oración, usamos ''which'' para añadir información sobre el restaurante. Es una cláusula relativa que especifica cuál restaurante.","stimulus_en":"The restaurant is very popular. It is in the city center.\n\nThe restaurant, which... ...","correct_answer":"is very popular, is in the city center.","example":"which","complexity":2,"concept_tags":["grammar","relative","clauses","unit"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U24_B3_I2', 'ingles-b1-U24', 12, 'key_word_transformation', 'Reescribe la frase usando la palabra clave ''who'':', '{"prompt_es":"Reescribe la frase usando la palabra clave ''who'':","mastery_tag":"grammar:b1-relative-clauses","explanation":"Aquí usamos ''who'' porque hablamos de una persona, y queremos añadir información sobre ella.","stimulus_en":"She is a doctor. She helps people.\n\nShe is a doctor who... ...","correct_answer":"helps people.","example":"who","complexity":2,"concept_tags":["grammar","relative","clauses","unit"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U24_B3_I3', 'ingles-b1-U24', 13, 'key_word_transformation', 'Reescribe la frase usando la palabra clave ''that'':', '{"prompt_es":"Reescribe la frase usando la palabra clave ''that'':","mastery_tag":"grammar:b1-relative-clauses","explanation":"Utilizamos ''that'' para dar más información sobre el museo, que es una cosa.","stimulus_en":"I visited the museum. It has ancient artifacts.\n\nI visited the museum that... ...","correct_answer":"has ancient artifacts.","example":"that","complexity":2,"concept_tags":["grammar","relative","clauses","unit"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U24_B3_I4', 'ingles-b1-U24', 14, 'key_word_transformation', 'Reescribe la frase usando la palabra clave ''which'':', '{"prompt_es":"Reescribe la frase usando la palabra clave ''which'':","mastery_tag":"grammar:b1-relative-clauses","explanation":"Aquí, ''which'' añade información sobre el libro, indicando quién lo escribió.","stimulus_en":"The book is on the table. It is written by J.K. Rowling.\n\nThe book, which... ...","correct_answer":"is written by J.K. Rowling, is on the table.","example":"which","complexity":2,"concept_tags":["grammar","relative","clauses","unit"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U24_B3_I5', 'ingles-b1-U24', 15, 'key_word_transformation', 'Reescribe la frase usando la palabra clave ''when'':', '{"prompt_es":"Reescribe la frase usando la palabra clave ''when'':","mastery_tag":"grammar:b1-relative-clauses","explanation":"Utilizamos ''when'' para referirnos a un momento específico en el tiempo, que es el día.","stimulus_en":"I remember the day. We met on that day.\n\nI remember the day when... ...","correct_answer":"we met.","example":"when","complexity":2,"concept_tags":["grammar","relative","clauses","unit"]}')
