INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U26_B1_I1', 'ingles-b1-U26', 1, 'multiple_choice', 'Choose the correct option to complete each sentence using relative clauses.', '{"prompt_es":"Choose the correct option to complete each sentence using relative clauses.","mastery_tag":"grammar:b1-relative-clauses","explanation":"La opción correcta es ''that'' porque se refiere al libro (sustantivo) que se ha mencionado. Usamos ''that'' para definir un sustantivo.","stimulus_en":"The book ____ I borrowed from you was fascinating.","options":[{"id":"o1","text":"that"},{"id":"o2","text":"who"},{"id":"o3","text":"where"},{"id":"o4","text":"when"}],"correct_answer":"o1","complexity":2,"concept_tags":["grammar","relative","clauses","animal","kingdom"]}')
