INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U26_B1_I2', 'ingles-b1-U26', 2, 'multiple_choice', 'Choose the correct option to complete each sentence using relative clauses.', '{"prompt_es":"Choose the correct option to complete each sentence using relative clauses.","mastery_tag":"grammar:b1-relative-clauses","explanation":"La opción correcta es ''who'' porque se refiere a una persona. ''Who'' se utiliza para describir a alguien.","stimulus_en":"She has a friend ____ lives in Italy.","options":[{"id":"o1","text":"who"},{"id":"o2","text":"which"},{"id":"o3","text":"that"},{"id":"o4","text":"when"}],"correct_answer":"o1","complexity":2,"concept_tags":["grammar","relative","clauses","animal","kingdom"]}')
