INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U26_B2_I1', 'ingles-b1-U26', 6, 'multiple_choice', 'Choose the correct answer to complete each sentence using the past continuous tense.', '{"prompt_es":"Choose the correct answer to complete each sentence using the past continuous tense.","mastery_tag":"grammar:b1-past-continuous","explanation":"Usamos ''was watching'' porque es una acción en progreso cuando ocurrió otra acción.","stimulus_en":"I _____ (watch) a movie when the lights went out.","options":[{"id":"o1","text":"was watching"},{"id":"o2","text":"watched"},{"id":"o3","text":"watching"},{"id":"o4","text":"is watching"}],"correct_answer":"o1","complexity":2,"concept_tags":["grammar","past","continuous","animal","kingdom"]}')
