INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U26_B2_I2', 'ingles-b1-U26', 7, 'multiple_choice', 'Choose the correct answer to complete each sentence using the past continuous tense.', '{"prompt_es":"Choose the correct answer to complete each sentence using the past continuous tense.","mastery_tag":"grammar:b1-past-continuous","explanation":"Debemos usar ''were playing'' porque se refiere a una acción en progreso en pasado.","stimulus_en":"While they _____ (play) football, it started to rain.","options":[{"id":"o1","text":"were playing"},{"id":"o2","text":"was playing"},{"id":"o3","text":"played"},{"id":"o4","text":"are playing"}],"correct_answer":"o1","complexity":2,"concept_tags":["grammar","past","continuous","animal","kingdom"]}')
