INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U26_B2_I3', 'ingles-b1-U26', 8, 'multiple_choice', 'Choose the correct answer to complete each sentence using the past continuous tense.', '{"prompt_es":"Choose the correct answer to complete each sentence using the past continuous tense.","mastery_tag":"grammar:b1-past-continuous","explanation":"La forma correcta es ''was studying'' porque la acción estaba en progreso.","stimulus_en":"She _____ (study) at the library when her friend called her.","options":[{"id":"o1","text":"was studying"},{"id":"o2","text":"studied"},{"id":"o3","text":"studies"},{"id":"o4","text":"is studying"}],"correct_answer":"o1","complexity":2,"concept_tags":["grammar","past","continuous","animal","kingdom"]}')
