INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U26_B2_I5', 'ingles-b1-U26', 10, 'multiple_choice', 'What _____ (you / do) when I called you last night?', '{"prompt_es":"What _____ (you / do) when I called you last night?","mastery_tag":"grammar:b1-past-continuous","explanation":"La respuesta correcta es ''were you doing'', que pregunta por una acción en progreso.","stimulus_en":"","options":[{"id":"o1","text":"were you doing"},{"id":"o2","text":"did you do"},{"id":"o3","text":"you were doing"},{"id":"o4","text":"you did"}],"correct_answer":"o1","complexity":2,"concept_tags":["grammar","past","continuous","animal","kingdom"]}')
