INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U27_B3_I1', 'ingles-b1-U27', 11, 'fill_blanks', 'Complete the sentences using the correct form of reported speech.', '{"prompt_es":"Complete the sentences using the correct form of reported speech.","mastery_tag":"grammar:b1-reported-speech","main_text":"She said, \"I am going to the party.\" → She said that she ___ to the party.","correct_answer":["was going","would finish","was","had seen","liked"],"explanation":"(1) En el discurso indirecto, hay un cambio de tiempo verbal. ''Am going'' se convierte en ''was going''.\n(2) El tiempo ''will'' cambia a ''would'' en el discurso indirecto.\n(3) El tiempo verbal cambia y se usa ''was'' para hacer la pregunta indirecta.\n(4) El tiempo perfecto ''have seen'' cambia a ''had seen'' en el discurso indirecto.\n(5) En el discurso indirecto, se utiliza el pasado simple ''liked'' en lugar del presente ''like''.","complexity":2,"concept_tags":["grammar","reported","speech","unit"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;

