INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U28_B3_I2', 'ingles-b1-U28', 4, 'fill_blanks', 'Complete the sentences with the correct form of the past continuous.', '{"prompt_es":"Complete the sentences with the correct form of the past continuous.","mastery_tag":"grammar:b1-past-continuous","explanation":"Se usa ''were'' porque ''they'' es plural, y la acción de jugar estaba en progreso.","stimulus_en":"Fill in the blanks using the past continuous tense.\n\nThey ___ (play) football when it started to rain.","correct_answer":"were playing","complexity":2,"concept_tags":["grammar","past","continuous","unit"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U28_B3_I3', 'ingles-b1-U28', 5, 'fill_blanks', 'Complete the sentences with the correct form of the past continuous.', '{"prompt_es":"Complete the sentences with the correct form of the past continuous.","mastery_tag":"grammar:b1-past-continuous","explanation":"El pasado continuo indica que la acción de cocinar estaba ocurriendo cuando ocurrió otra acción.","stimulus_en":"Fill in the blanks using the past continuous tense.\n\nWhile she ___ (cook), the phone rang.","correct_answer":"was cooking","complexity":2,"concept_tags":["grammar","past","continuous","unit"]}')
