INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U29_B1_I2', 'ingles-b1-U29', 2, 'multiple_choice', 'She asked, ''Where are you going?'' She _____ where I was going.', '{"prompt_es":"She asked, ''Where are you going?'' She _____ where I was going.","mastery_tag":"grammar:b1-reported-speech","explanation":"Cuando reportamos preguntas, usamos ''asked'' para la forma indirecta.","stimulus_en":"","options":[{"id":"o1","text":"asked"},{"id":"o2","text":"questioned"},{"id":"o3","text":"told"},{"id":"o4","text":"said"}],"correct_answer":"o1","complexity":2,"concept_tags":["grammar","reported","speech","unit"]}')
