VALUES ('B1_U29_B2_I1', 'ingles-b1-U29', 6, 'multiple_choice', 'What is a common method to reduce waste in our daily lives?', '{"prompt_es":"What is a common method to reduce waste in our daily lives?","mastery_tag":"vocabulary:b1-environment","explanation":"Reciclar es un método común para reducir los desechos y fomentar la sostenibilidad.","stimulus_en":"","options":[{"id":"o1","text":"Recycling"},{"id":"o2","text":"Burning"},{"id":"o3","text":"Throwing"},{"id":"o4","text":"Ignoring"}],"correct_answer":"o1","complexity":2,"concept_tags":["vocabulary","environment","unit","grammar"]}')
