VALUES ('B1_U29_B2_I2', 'ingles-b1-U29', 7, 'multiple_choice', 'Which gas is mainly responsible for climate change?', '{"prompt_es":"Which gas is mainly responsible for climate change?","mastery_tag":"vocabulary:b1-environment","explanation":"El dióxido de carbono es un gas de efecto invernadero que contribuye al cambio climático.","stimulus_en":"","options":[{"id":"o1","text":"Oxygen"},{"id":"o2","text":"Nitrogen"},{"id":"o3","text":"Carbon Dioxide"},{"id":"o4","text":"Helium"}],"correct_answer":"o3","complexity":2,"concept_tags":["vocabulary","environment","unit","grammar"]}')
