INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U30_B1_I1', 'ingles-b1-U30', 1, 'fill_blanks', 'Complete the sentences with the correct words.', '{"prompt_es":"Complete the sentences with the correct words.","mastery_tag":"writing:b1-informal-letters","main_text":"Dear Sarah, I hope you are doing ___.\n\nI just wanted to say thank you for your ___.\n\nHow is your family? Please say hello to ___.\n\nI have been thinking about our last ___.\n\nLet''s plan a trip to the beach next ___.","correct_answer":["well","gift","them","meeting","weekend"],"explanation":"(1) En una carta informal, preguntar por el bienestar de alguien es común.\n(2) Usamos ''gift'' para referirnos a algo que le das a alguien como muestra de aprecio.\n(3) En este contexto, ''them'' se refiere a la familia de la persona a la que escribes.\n(4) En un contexto informal, ''meeting'' puede referirse a un encuentro o una conversación.\n(5) El ''weekend'' es el tiempo ideal para planear actividades recreativas.","complexity":2,"concept_tags":["writing","informal","letters","unit"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U30_B2_I1', 'ingles-b1-U30', 2, 'key_word_transformation', 'Reescribe la frase usando la palabra clave ''have been'':', '{"prompt_es":"Reescribe la frase usando la palabra clave ''have been'':","mastery_tag":"grammar:b1-present-perfect-continuous","explanation":"Usamos ''for'' para indicar la duración desde el pasado hasta el presente.","stimulus_en":"I started working here two years ago. (have been)\n\nI have been working here ...","correct_answer":"for two years.","example":"have been","complexity":2,"concept_tags":["grammar","present","perfect","continuous","unit"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U30_B2_I2', 'ingles-b1-U30', 3, 'key_word_transformation', 'Reescribe la frase usando la palabra clave ''has been'':', '{"prompt_es":"Reescribe la frase usando la palabra clave ''has been'':","mastery_tag":"grammar:b1-present-perfect-continuous","explanation":"Usamos ''since'' para indicar el inicio de una acción en un punto específico del tiempo.","stimulus_en":"She is studying English since last year. (has been)\n\nShe has been studying English ...","correct_answer":"since last year.","example":"has been","complexity":2,"concept_tags":["grammar","present","perfect","continuous","unit"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U30_B2_I3', 'ingles-b1-U30', 4, 'key_word_transformation', 'Reescribe la frase usando la palabra clave ''have been'':', '{"prompt_es":"Reescribe la frase usando la palabra clave ''have been'':","mastery_tag":"grammar:b1-present-perfect-continuous","explanation":"Se utiliza ''for'' para indicar la duración de tiempo hasta ahora.","stimulus_en":"They started playing football six months ago. (have been)\n\nThey have been playing football ...","correct_answer":"for six months.","example":"have been","complexity":2,"concept_tags":["grammar","present","perfect","continuous","unit"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U30_B2_I4', 'ingles-b1-U30', 5, 'key_word_transformation', 'Reescribe la frase usando la palabra clave ''have been'':', '{"prompt_es":"Reescribe la frase usando la palabra clave ''have been'':","mastery_tag":"grammar:b1-present-perfect-continuous","explanation":"Usamos ''since'' para indicar un punto específico en el tiempo.","stimulus_en":"I began learning French last summer. (have been)\n\nI have been learning French ...","correct_answer":"since last summer.","example":"have been","complexity":2,"concept_tags":["grammar","present","perfect","continuous","unit"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U30_B2_I5', 'ingles-b1-U30', 6, 'key_word_transformation', 'Reescribe la frase usando la palabra clave ''has been'':', '{"prompt_es":"Reescribe la frase usando la palabra clave ''has been'':","mastery_tag":"grammar:b1-present-perfect-continuous","explanation":"Usamos ''for'' para la duración de la acción desde el pasado.","stimulus_en":"He started working out three weeks ago. (has been)\n\nHe has been working out ...","correct_answer":"for three weeks.","example":"has been","complexity":2,"concept_tags":["grammar","present","perfect","continuous","unit"]}')
