VALUES ('B1_U33_B1_I2', 'ingles-b1-U33', 2, 'multiple_choice', 'Choose the correct option to complete each sentence with the present perfect continuous tense.', '{"prompt_es":"Choose the correct option to complete each sentence with the present perfect continuous tense.","mastery_tag":"grammar:b1-present-perfect-continuous","explanation":"El uso de ''has been training'' indica que la acción empezó en el pasado y sigue hasta el presente.","stimulus_en":"She ___ (train) for the marathon since last year.","options":[{"id":"o1","text":"has been training"},{"id":"o2","text":"have been training"},{"id":"o3","text":"was training"},{"id":"o4","text":"has trained"}],"correct_answer":"o1","complexity":2,"concept_tags":["grammar","present","perfect","continuous","unit"]}')
