INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U33_B1_I4', 'ingles-b1-U33', 4, 'multiple_choice', 'Choose the correct option to complete each sentence with the present perfect continuous tense.', '{"prompt_es":"Choose the correct option to complete each sentence with the present perfect continuous tense.","mastery_tag":"grammar:b1-present-perfect-continuous","explanation":"El tiempo presente perfecto continuo ''have been waiting'' muestra la duración de una acción que aún está en curso.","stimulus_en":"They ___ (wait) for the results since the last meeting.","options":[{"id":"o1","text":"have waited"},{"id":"o2","text":"have been waiting"},{"id":"o3","text":"waited"},{"id":"o4","text":"has been waiting"}],"correct_answer":"o2","complexity":2,"concept_tags":["grammar","present","perfect","continuous","unit"]}')
