INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U33_B1_I5', 'ingles-b1-U33', 5, 'multiple_choice', 'Choose the correct option to complete each sentence with the present perfect continuous tense.', '{"prompt_es":"Choose the correct option to complete each sentence with the present perfect continuous tense.","mastery_tag":"grammar:b1-present-perfect-continuous","explanation":"''Has been teaching'' indica que la acción de enseñar comenzó en el pasado y continua hasta ahora.","stimulus_en":"He ___ (teach) English for five years.","options":[{"id":"o1","text":"has taught"},{"id":"o2","text":"has been teaching"},{"id":"o3","text":"have taught"},{"id":"o4","text":"is teaching"}],"correct_answer":"o2","complexity":2,"concept_tags":["grammar","present","perfect","continuous","unit"]}')
