INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U34_B1_I1', 'ingles-b1-U34', 1, 'key_word_transformation', 'Reescribe la frase usando la palabra clave ''FOR'':', '{"prompt_es":"Reescribe la frase usando la palabra clave ''FOR'':","mastery_tag":"grammar:b1-present-perfect-continuous","explanation":"Esta respuesta usa el presente perfecto continuo para indicar que una acción comenzó en el pasado y continúa hasta el presente.","stimulus_en":"I started studying English two years ago. (for)\n\nI have been studying English... ...","correct_answer":"for two years.","example":"FOR","complexity":2,"concept_tags":["grammar","present","perfect","continuous","unit"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U34_B1_I2', 'ingles-b1-U34', 2, 'key_word_transformation', 'Reescribe la frase usando la palabra clave ''SINCE'':', '{"prompt_es":"Reescribe la frase usando la palabra clave ''SINCE'':","mastery_tag":"grammar:b1-present-perfect-continuous","explanation":"Usamos ''since'' para decir cuándo comenzó la acción que sigue hasta ahora.","stimulus_en":"She began working here in 2018. (since)\n\nShe has been working here... ...","correct_answer":"since 2018.","example":"SINCE","complexity":2,"concept_tags":["grammar","present","perfect","continuous","unit"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U34_B1_I3', 'ingles-b1-U34', 3, 'key_word_transformation', 'Reescribe la frase usando la palabra clave ''FOR'':', '{"prompt_es":"Reescribe la frase usando la palabra clave ''FOR'':","mastery_tag":"grammar:b1-present-perfect-continuous","explanation":"Aquí se indica la duración de una acción que comenzó en el pasado y sigue en el presente.","stimulus_en":"They started playing football last year. (for)\n\nThey have been playing football... ...","correct_answer":"for a year.","example":"FOR","complexity":2,"concept_tags":["grammar","present","perfect","continuous","unit"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U34_B1_I4', 'ingles-b1-U34', 4, 'key_word_transformation', 'Reescribe la frase usando la palabra clave ''SINCE'':', '{"prompt_es":"Reescribe la frase usando la palabra clave ''SINCE'':","mastery_tag":"grammar:b1-present-perfect-continuous","explanation":"Usamos ''since'' para indicar el tiempo específico en que comenzó la acción.","stimulus_en":"He has a headache because he studied all day. (since)\n\nHe has been studying all day... ...","correct_answer":"since this morning.","example":"SINCE","complexity":2,"concept_tags":["grammar","present","perfect","continuous","unit"]}')
