VALUES ('B1_U34_B1_I5', 'ingles-b1-U34', 5, 'key_word_transformation', 'Reescribe la frase usando la palabra clave ''FOR'':', '{"prompt_es":"Reescribe la frase usando la palabra clave ''FOR'':","mastery_tag":"grammar:b1-present-perfect-continuous","explanation":"Esto muestra que la acción de aprender a tocar guitarra comenzó en el pasado y continúa hasta ahora.","stimulus_en":"I started learning to play guitar six months ago. (for)\n\nI have been learning to play guitar... ...","correct_answer":"for six months.","example":"FOR","complexity":2,"concept_tags":["grammar","present","perfect","continuous","unit"]}')
