VALUES ('B1_U34_B2_I3', 'ingles-b1-U34', 8, 'multiple_choice', 'Choose the correct passive form of the sentences below.', '{"prompt_es":"Choose the correct passive form of the sentences below.","mastery_tag":"grammar:b1-passive","explanation":"La forma pasiva pasada se forma con ''were'' y el participio pasado ''sent''.","stimulus_en":"The letters ___ yesterday.","options":[{"id":"o1","text":"were sent"},{"id":"o2","text":"send"},{"id":"o3","text":"was sent"},{"id":"o4","text":"sending"}],"correct_answer":"o1","complexity":2,"concept_tags":["grammar","passive","unit"]}')
