VALUES ('B1_U34_B2_I4', 'ingles-b1-U34', 9, 'multiple_choice', 'Choose the correct passive form of the sentences below.', '{"prompt_es":"Choose the correct passive form of the sentences below.","mastery_tag":"grammar:b1-passive","explanation":"Usamos ''are'' para hablar sobre el futuro en voz pasiva.","stimulus_en":"The results ___ next week.","options":[{"id":"o1","text":"are announced"},{"id":"o2","text":"were announced"},{"id":"o3","text":"is announced"},{"id":"o4","text":"announced"}],"correct_answer":"o1","complexity":2,"concept_tags":["grammar","passive","unit"]}')
