INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U36_B1_I1', 'ingles-b1-U36', 1, 'multiple_choice', 'Choose the best option to complete each sentence about informal letters and emails.', '{"prompt_es":"Choose the best option to complete each sentence about informal letters and emails.","mastery_tag":"writing:b1-informal-letters","explanation":"En las cartas informales, normalmente usamos saludos como ''Hi'' o ''Hello'', no ''Dear Sir'' o ''To whom it may concern'', que son más formales.","stimulus_en":"When starting an informal letter, you usually write _____.","options":[{"id":"o1","text":"Dear Sir"},{"id":"o2","text":"Hi"},{"id":"o3","text":"To whom it may concern"},{"id":"o4","text":"Hello Mr."}],"correct_answer":"o2","complexity":2,"concept_tags":["writing","informal","letters","unit","grammar"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U36_B1_I2', 'ingles-b1-U36', 2, 'multiple_choice', 'Choose the best option to complete each sentence about informal letters and emails.', '{"prompt_es":"Choose the best option to complete each sentence about informal letters and emails.","mastery_tag":"writing:b1-informal-letters","explanation":"En un email informal, es común preguntar sobre la familia o la vida personal de un amigo, a diferencia de asuntos de negocios o legales.","stimulus_en":"In an informal email, you might ask about _____.","options":[{"id":"o1","text":"business matters"},{"id":"o2","text":"your friend''s family"},{"id":"o3","text":"company policies"},{"id":"o4","text":"legal issues"}],"correct_answer":"o2","complexity":2,"concept_tags":["writing","informal","letters","unit","grammar"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U36_B1_I3', 'ingles-b1-U36', 3, 'multiple_choice', 'Choose the best option to complete each sentence about informal letters and emails.', '{"prompt_es":"Choose the best option to complete each sentence about informal letters and emails.","mastery_tag":"writing:b1-informal-letters","explanation":"Cierres como ''Best wishes'' son apropiados para cartas informales. ''Sincerely'' y ''Yours faithfully'' son más formales.","stimulus_en":"To close an informal letter, you can say _____.","options":[{"id":"o1","text":"Sincerely"},{"id":"o2","text":"Warm regards"},{"id":"o3","text":"Best wishes"},{"id":"o4","text":"Yours faithfully"}],"correct_answer":"o3","complexity":2,"concept_tags":["writing","informal","letters","unit","grammar"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U36_B1_I4', 'ingles-b1-U36', 4, 'multiple_choice', 'Which of the following is NOT typical in an informal letter? _____.', '{"prompt_es":"Which of the following is NOT typical in an informal letter? _____.","mastery_tag":"writing:b1-informal-letters","explanation":"En las cartas informales, evitamos un tono formal y usamos un lenguaje más relajado y personal.","stimulus_en":"","options":[{"id":"o1","text":"Sharing personal news"},{"id":"o2","text":"Using slang"},{"id":"o3","text":"Writing in a formal tone"},{"id":"o4","text":"Asking for opinions"}],"correct_answer":"o3","complexity":2,"concept_tags":["writing","informal","letters","unit","grammar"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U36_B1_I5', 'ingles-b1-U36', 5, 'multiple_choice', 'Choose the best option to complete each sentence about informal letters and emails.', '{"prompt_es":"Choose the best option to complete each sentence about informal letters and emails.","mastery_tag":"writing:b1-informal-letters","explanation":"Incluir historias divertidas hace que la carta sea más amena y personal, lo cual es adecuado para amigos.","stimulus_en":"When writing to a friend, it’s important to include _____.","options":[{"id":"o1","text":"professional updates"},{"id":"o2","text":"funny stories"},{"id":"o3","text":"bank details"},{"id":"o4","text":"strict rules"}],"correct_answer":"o2","complexity":2,"concept_tags":["writing","informal","letters","unit","grammar"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U36_B2_I1', 'ingles-b1-U36', 6, 'multiple_choice', 'Which phrase shows agreement with an opinion?', '{"prompt_es":"Which phrase shows agreement with an opinion?","mastery_tag":"speaking:b1-expressing-opinions","explanation":"La frase ''I think so'' muestra que estás de acuerdo con la opinión de alguien más.","stimulus_en":"","options":[{"id":"o1","text":"I think so"},{"id":"o2","text":"I disagree"},{"id":"o3","text":"I''m not sure"},{"id":"o4","text":"That''s interesting"}],"correct_answer":"o1","complexity":2,"concept_tags":["speaking","expressing","opinions","unit","grammar"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U36_B2_I2', 'ingles-b1-U36', 7, 'multiple_choice', 'What is a good way to express a contrasting opinion?', '{"prompt_es":"What is a good way to express a contrasting opinion?","mastery_tag":"speaking:b1-expressing-opinions","explanation":"La palabra ''However'' se usa para presentar un contraste o una opinión diferente.","stimulus_en":"","options":[{"id":"o1","text":"In my opinion"},{"id":"o2","text":"However"},{"id":"o3","text":"I believe"},{"id":"o4","text":"Absolutely"}],"correct_answer":"o2","complexity":2,"concept_tags":["speaking","expressing","opinions","unit","grammar"]}')
