INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U36_B2_I3', 'ingles-b1-U36', 8, 'multiple_choice', 'Which sentence provides a reason for an opinion?', '{"prompt_es":"Which sentence provides a reason for an opinion?","mastery_tag":"speaking:b1-expressing-opinions","explanation":"La oración ''Reading is relaxing.'' da una razón para disfrutar de la lectura.","stimulus_en":"","options":[{"id":"o1","text":"I enjoy reading."},{"id":"o2","text":"Reading is relaxing."},{"id":"o3","text":"I do not like it."},{"id":"o4","text":"That''s a good point."}],"correct_answer":"o2","complexity":2,"concept_tags":["speaking","expressing","opinions","unit","grammar"]}')
