INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U36_B2_I4', 'ingles-b1-U36', 9, 'multiple_choice', 'If you disagree, which phrase can you use?', '{"prompt_es":"If you disagree, which phrase can you use?","mastery_tag":"speaking:b1-expressing-opinions","explanation":"La frase ''I don’t think that’s true'' indica desacuerdo con la opinión expresada.","stimulus_en":"","options":[{"id":"o1","text":"I don’t think that’s true"},{"id":"o2","text":"I agree completely"},{"id":"o3","text":"That''s a good idea"},{"id":"o4","text":"You are right"}],"correct_answer":"o1","complexity":2,"concept_tags":["speaking","expressing","opinions","unit","grammar"]}')
