INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U36_B2_I5', 'ingles-b1-U36', 10, 'multiple_choice', 'What is a polite way to ask for someone''s opinion?', '{"prompt_es":"What is a polite way to ask for someone''s opinion?","mastery_tag":"speaking:b1-expressing-opinions","explanation":"La pregunta ''What do you think?'' es una forma educada de pedir la opinión de alguien.","stimulus_en":"","options":[{"id":"o1","text":"What do you think?"},{"id":"o2","text":"I know best"},{"id":"o3","text":"That''s wrong"},{"id":"o4","text":"Don''t you agree?"}],"correct_answer":"o1","complexity":2,"concept_tags":["speaking","expressing","opinions","unit","grammar"]}')
