INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U36_B3_I1', 'ingles-b1-U36', 11, 'multiple_choice', 'Choose the correct option to complete each sentence.', '{"prompt_es":"Choose the correct option to complete each sentence.","mastery_tag":"grammar:b1-relative-clauses","explanation":"En este caso, se utiliza ''which'' porque se refiere a un objeto, en este caso, ''the book''.","stimulus_en":"The book ____ I borrowed from you is very interesting.","options":[{"id":"o1","text":"who"},{"id":"o2","text":"which"},{"id":"o3","text":"where"},{"id":"o4","text":"that"}],"correct_answer":"o2","complexity":2,"concept_tags":["grammar","relative","clauses","unit"]}')
