VALUES ('B1_U36_B3_I4', 'ingles-b1-U36', 14, 'multiple_choice', 'Choose the correct option to complete each sentence.', '{"prompt_es":"Choose the correct option to complete each sentence.","mastery_tag":"grammar:b1-relative-clauses","explanation":"Usamos ''when'' para referirnos a momentos temporales, como en este caso, ''the day''.","stimulus_en":"I remember the day ____ we first met.","options":[{"id":"o1","text":"when"},{"id":"o2","text":"who"},{"id":"o3","text":"which"},{"id":"o4","text":"that"}],"correct_answer":"o1","complexity":2,"concept_tags":["grammar","relative","clauses","unit"]}')
