VALUES ('B1_U36_B3_I5', 'ingles-b1-U36', 15, 'multiple_choice', 'Choose the correct option to complete each sentence.', '{"prompt_es":"Choose the correct option to complete each sentence.","mastery_tag":"grammar:b1-relative-clauses","explanation":"En este caso, ''that'' se usa para especificar un objeto, que en este caso es ''the car''.","stimulus_en":"This is the car ____ belongs to my brother.","options":[{"id":"o1","text":"that"},{"id":"o2","text":"which"},{"id":"o3","text":"who"},{"id":"o4","text":"when"}],"correct_answer":"o1","complexity":2,"concept_tags":["grammar","relative","clauses","unit"]}')
