INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U37_B1_I5', 'ingles-b1-U37', 5, 'multiple_choice', 'Choose the correct answer to complete each sentence using the Present Perfect Continuous tense.', '{"prompt_es":"Choose the correct answer to complete each sentence using the Present Perfect Continuous tense.","mastery_tag":"grammar:b1-present-perfect-continuous","explanation":"La respuesta adecuada es ''has been living'' porque se refiere a una acción que comenzó en el pasado y que sigue hasta ahora.","stimulus_en":"He ___ (live) in London since 2010.","options":[{"id":"o1","text":"has living"},{"id":"o2","text":"has lived"},{"id":"o3","text":"has been living"},{"id":"o4","text":"is living"}],"correct_answer":"o3","complexity":2,"concept_tags":["grammar","present","perfect","continuous","unit"]}')
