INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U40_B1_I1', 'ingles-b1-U40', 1, 'multiple_choice', 'What is the main purpose of a story''s introduction?', '{"prompt_es":"What is the main purpose of a story''s introduction?","mastery_tag":"writing:b1-stories","explanation":"La introducción de una historia establece el escenario y presenta a los personajes principales.","stimulus_en":"","options":[{"id":"o1","text":"To create suspense"},{"id":"o2","text":"To introduce characters"},{"id":"o3","text":"To conclude the story"},{"id":"o4","text":"To provide details"}],"correct_answer":"o2","complexity":2,"concept_tags":["writing","stories","unit","grammar"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U40_B1_I2', 'ingles-b1-U40', 2, 'multiple_choice', 'What should typically happen in the climax of a story?', '{"prompt_es":"What should typically happen in the climax of a story?","mastery_tag":"writing:b1-stories","explanation":"El clímax es el momento de mayor tensión donde se resuelve el problema principal de la historia.","stimulus_en":"","options":[{"id":"o1","text":"The story ends"},{"id":"o2","text":"A problem is resolved"},{"id":"o3","text":"New characters appear"},{"id":"o4","text":"The setting changes"}],"correct_answer":"o2","complexity":2,"concept_tags":["writing","stories","unit","grammar"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U40_B1_I3', 'ingles-b1-U40', 3, 'multiple_choice', 'What is the purpose of the conclusion in a story?', '{"prompt_es":"What is the purpose of the conclusion in a story?","mastery_tag":"writing:b1-stories","explanation":"La conclusión resume lo que ha sucedido y puede ofrecer una reflexión final.","stimulus_en":"","options":[{"id":"o1","text":"To introduce new characters"},{"id":"o2","text":"To summarize events"},{"id":"o3","text":"To create a conflict"},{"id":"o4","text":"To start a new plot"}],"correct_answer":"o2","complexity":2,"concept_tags":["writing","stories","unit","grammar"]}')
