VALUES ('B1_U40_B1_I4', 'ingles-b1-U40', 4, 'multiple_choice', 'Which of the following is important for developing characters?', '{"prompt_es":"Which of the following is important for developing characters?","mastery_tag":"writing:b1-stories","explanation":"Desarrollar personajes implica mostrar sus emociones y motivaciones para que sean creíbles.","stimulus_en":"","options":[{"id":"o1","text":"Using complex vocabulary"},{"id":"o2","text":"Showing their emotions"},{"id":"o3","text":"Making them perfect"},{"id":"o4","text":"Limiting dialogue"}],"correct_answer":"o2","complexity":2,"concept_tags":["writing","stories","unit","grammar"]}')
