INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U41_B1_I1', 'ingles-b1-U41', 1, 'fill_blanks', 'Complete the sentences using the passive voice.', '{"prompt_es":"Complete the sentences using the passive voice.","mastery_tag":"grammar:b1-passive","main_text":"1. The book ___ (write) by a famous author. 2. The project ___ (complete) last week. 3. The song ___ (sing) by the choir during the concert. 4. The dinner ___ (prepare) by my mother yesterday. 5. The results ___ (announce) at the meeting tomorrow.","correct_answer":["was written","was completed","was sung","was prepared","will be announced"],"explanation":"(1) En pasiva, ''was written'' indica que la acción fue realizada por alguien en el pasado.\n(2) Se usa ''was completed'' porque la acción ocurrió en el pasado.\n(3) El verbo ''sing'' se convierte en ''sung'' en forma pasiva.\n(4) Aquí usamos ''was prepared'' para indicar que alguien hizo la acción en el pasado.\n(5) Utilizamos la forma futura pasiva ''will be announced'' porque la acción ocurrirá en el futuro.","complexity":2,"concept_tags":["grammar","passive","unit"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U41_B2_I1', 'ingles-b1-U41', 2, 'fill_blanks', 'Complete the sentences with the correct words.', '{"prompt_es":"Complete the sentences with the correct words.","mastery_tag":"writing:b1-informal-letters","explanation":"En este contexto, la palabra correcta es ''vacation'' porque se refiere a un viaje o tiempo libre.","stimulus_en":"Dear Sarah,\n\nI hope you are doing well! I wanted to tell you about my recent vacation. I went to the beach and it was really amazing. The weather was perfect and I spent most of my time swimming and relaxing.\n\nI also tried some delicious food. There were many restaurants by the beach. I loved the seafood!\n\nI can''t wait to hear about your summer plans. Let me know when we can catch up.\n\nTake care,\n\nEmily\n\nI wanted to tell you about my recent ___ .","correct_answer":"vacation","complexity":2,"concept_tags":["writing","informal","letters","unit"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U41_B2_I2', 'ingles-b1-U41', 3, 'fill_blanks', 'Complete the sentences with the correct words.', '{"prompt_es":"Complete the sentences with the correct words.","mastery_tag":"writing:b1-informal-letters","explanation":"La palabra ''perfect'' describe condiciones climáticas ideales para disfrutar del tiempo al aire libre.","stimulus_en":"Dear Sarah,\n\nI hope you are doing well! I wanted to tell you about my recent vacation. I went to the beach and it was really amazing. The weather was perfect and I spent most of my time swimming and relaxing.\n\nI also tried some delicious food. There were many restaurants by the beach. I loved the seafood!\n\nI can''t wait to hear about your summer plans. Let me know when we can catch up.\n\nTake care,\n\nEmily\n\nThe weather was ___ and I spent most of my time swimming.","correct_answer":"perfect","complexity":2,"concept_tags":["writing","informal","letters","unit"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U41_B2_I3', 'ingles-b1-U41', 4, 'fill_blanks', 'Complete the sentences with the correct words.', '{"prompt_es":"Complete the sentences with the correct words.","mastery_tag":"writing:b1-informal-letters","explanation":"La palabra ''delicious'' se utiliza para expresar que la comida tiene buen sabor.","stimulus_en":"Dear Sarah,\n\nI hope you are doing well! I wanted to tell you about my recent vacation. I went to the beach and it was really amazing. The weather was perfect and I spent most of my time swimming and relaxing.\n\nI also tried some delicious food. There were many restaurants by the beach. I loved the seafood!\n\nI can''t wait to hear about your summer plans. Let me know when we can catch up.\n\nTake care,\n\nEmily\n\nI also tried some ___ food.","correct_answer":"delicious","complexity":2,"concept_tags":["writing","informal","letters","unit"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U41_B2_I4', 'ingles-b1-U41', 5, 'fill_blanks', 'Complete the sentences with the correct words.', '{"prompt_es":"Complete the sentences with the correct words.","mastery_tag":"writing:b1-informal-letters","explanation":"En este contexto, ''beach'' se refiere al lugar donde la tierra se encuentra con el agua, ideal para vacaciones.","stimulus_en":"Dear Sarah,\n\nI hope you are doing well! I wanted to tell you about my recent vacation. I went to the beach and it was really amazing. The weather was perfect and I spent most of my time swimming and relaxing.\n\nI also tried some delicious food. There were many restaurants by the beach. I loved the seafood!\n\nI can''t wait to hear about your summer plans. Let me know when we can catch up.\n\nTake care,\n\nEmily\n\nThere were many restaurants by the ___ .","correct_answer":"beach","complexity":2,"concept_tags":["writing","informal","letters","unit"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U41_B2_I5', 'ingles-b1-U41', 6, 'fill_blanks', 'Complete the sentences with the correct words.', '{"prompt_es":"Complete the sentences with the correct words.","mastery_tag":"writing:b1-informal-letters","explanation":"''Catch up'' es una expresión común que significa reunirse o hablar para actualizarse sobre la vida de cada uno.","stimulus_en":"Dear Sarah,\n\nI hope you are doing well! I wanted to tell you about my recent vacation. I went to the beach and it was really amazing. The weather was perfect and I spent most of my time swimming and relaxing.\n\nI also tried some delicious food. There were many restaurants by the beach. I loved the seafood!\n\nI can''t wait to hear about your summer plans. Let me know when we can catch up.\n\nTake care,\n\nEmily\n\nLet me know when we can ___ up.","correct_answer":"catch","complexity":2,"concept_tags":["writing","informal","letters","unit"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U41_B3_I1', 'ingles-b1-U41', 7, 'multiple_choice', 'Choose the correct relative pronoun to complete each sentence.', '{"prompt_es":"Choose the correct relative pronoun to complete each sentence.","mastery_tag":"grammar:b1-relative-clauses","explanation":"En esta oración, usamos ''which'' porque se refiere a un objeto (el libro).","stimulus_en":"The book ___ I borrowed is very interesting.","options":[{"id":"o1","text":"who"},{"id":"o2","text":"which"},{"id":"o3","text":"where"},{"id":"o4","text":"that"}],"correct_answer":"o2","complexity":2,"concept_tags":["grammar","relative","clauses","unit"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U41_B3_I2', 'ingles-b1-U41', 8, 'multiple_choice', 'Choose the correct relative pronoun to complete each sentence.', '{"prompt_es":"Choose the correct relative pronoun to complete each sentence.","mastery_tag":"grammar:b1-relative-clauses","explanation":"Utilizamos ''who'' porque nos referimos a una persona (la artista).","stimulus_en":"She is the artist ___ painted the mural.","options":[{"id":"o1","text":"who"},{"id":"o2","text":"whose"},{"id":"o3","text":"that"},{"id":"o4","text":"which"}],"correct_answer":"o1","complexity":2,"concept_tags":["grammar","relative","clauses","unit"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U41_B3_I3', 'ingles-b1-U41', 9, 'multiple_choice', 'Choose the correct relative pronoun to complete each sentence.', '{"prompt_es":"Choose the correct relative pronoun to complete each sentence.","mastery_tag":"grammar:b1-relative-clauses","explanation":"Se debe usar ''where'' porque estamos hablando de un lugar (el restaurante).","stimulus_en":"The restaurant ___ we went to last night was closed.","options":[{"id":"o1","text":"which"},{"id":"o2","text":"that"},{"id":"o3","text":"where"},{"id":"o4","text":"who"}],"correct_answer":"o3","complexity":2,"concept_tags":["grammar","relative","clauses","unit"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U41_B3_I4', 'ingles-b1-U41', 10, 'multiple_choice', 'Choose the correct relative pronoun to complete each sentence.', '{"prompt_es":"Choose the correct relative pronoun to complete each sentence.","mastery_tag":"grammar:b1-relative-clauses","explanation":"''That'' se usa aquí para referirse al objeto (la película) en la oración.","stimulus_en":"This is the movie ___ I told you about.","options":[{"id":"o1","text":"who"},{"id":"o2","text":"where"},{"id":"o3","text":"which"},{"id":"o4","text":"that"}],"correct_answer":"o4","complexity":2,"concept_tags":["grammar","relative","clauses","unit"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U41_B3_I5', 'ingles-b1-U41', 11, 'multiple_choice', 'Choose the correct relative pronoun to complete each sentence.', '{"prompt_es":"Choose the correct relative pronoun to complete each sentence.","mastery_tag":"grammar:b1-relative-clauses","explanation":"''Who'' se utiliza para referirse a personas y aquí habla de mi hermano.","stimulus_en":"My brother, ___ lives in Canada, is visiting next week.","options":[{"id":"o1","text":"who"},{"id":"o2","text":"that"},{"id":"o3","text":"where"},{"id":"o4","text":"when"}],"correct_answer":"o1","complexity":2,"concept_tags":["grammar","relative","clauses","unit"]}')
