INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U42_B2_I2', 'ingles-b1-U42', 7, 'multiple_choice', 'How should you dress for an interview?', '{"prompt_es":"How should you dress for an interview?","mastery_tag":"listening:b1-interviews","explanation":"Vestirse formalmente muestra profesionalismo en una entrevista.","stimulus_en":"","options":[{"id":"o1","text":"Casually"},{"id":"o2","text":"Informally"},{"id":"o3","text":"Formally"},{"id":"o4","text":"Colorfully"}],"correct_answer":"o3","complexity":2,"concept_tags":["listening","interviews","unit","grammar"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U42_B2_I3', 'ingles-b1-U42', 8, 'multiple_choice', 'What is a good way to start an interview?', '{"prompt_es":"What is a good way to start an interview?","mastery_tag":"listening:b1-interviews","explanation":"Saludar al entrevistador establece un ambiente amigable y profesional.","stimulus_en":"","options":[{"id":"o1","text":"Be quiet"},{"id":"o2","text":"Greet the interviewer"},{"id":"o3","text":"Ask about money"},{"id":"o4","text":"Talk about friends"}],"correct_answer":"o2","complexity":2,"concept_tags":["listening","interviews","unit","grammar"]}')
