INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U42_B2_I4', 'ingles-b1-U42', 9, 'multiple_choice', 'What should you prepare before the interview?', '{"prompt_es":"What should you prepare before the interview?","mastery_tag":"listening:b1-interviews","explanation":"Preparar preguntas muestra interés y preparación ante el entrevistador.","stimulus_en":"","options":[{"id":"o1","text":"Questions to ask"},{"id":"o2","text":"A lunch"},{"id":"o3","text":"A movie"},{"id":"o4","text":"A gift"}],"correct_answer":"o1","complexity":2,"concept_tags":["listening","interviews","unit","grammar"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U42_B2_I5', 'ingles-b1-U42', 10, 'multiple_choice', 'What is important to do during the interview?', '{"prompt_es":"What is important to do during the interview?","mastery_tag":"listening:b1-interviews","explanation":"Hablar de manera clara ayuda a que el entrevistador entienda tus respuestas.","stimulus_en":"","options":[{"id":"o1","text":"Interrupt the interviewer"},{"id":"o2","text":"Speak clearly"},{"id":"o3","text":"Avoid eye contact"},{"id":"o4","text":"Talk too fast"}],"correct_answer":"o2","complexity":2,"concept_tags":["listening","interviews","unit","grammar"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U42_B3_I1', 'ingles-b1-U42', 11, 'key_word_transformation', 'Reescribe la frase usando la palabra clave ''approve'':', '{"prompt_es":"Reescribe la frase usando la palabra clave ''approve'':","mastery_tag":"grammar:b1-passive","explanation":"Aquí se utiliza la voz pasiva para enfatizar la acción sobre el sujeto. Se utiliza ''was'' seguido del participio ''approved''.","stimulus_en":"The manager approved the proposal. (KEY: approve)\n\nThe proposal was approved... ...","correct_answer":"by the manager.","example":"approve","complexity":2,"concept_tags":["grammar","passive","unit"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U42_B3_I2', 'ingles-b1-U42', 12, 'key_word_transformation', 'Reescribe la frase usando la palabra clave ''complete'':', '{"prompt_es":"Reescribe la frase usando la palabra clave ''complete'':","mastery_tag":"grammar:b1-passive","explanation":"Usamos la voz pasiva para resaltar el resultado de la acción. Se utiliza ''was'' y el participio ''completed''.","stimulus_en":"The team completed the project. (KEY: complete)\n\nThe project was completed... ...","correct_answer":"by the team.","example":"complete","complexity":2,"concept_tags":["grammar","passive","unit"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U42_B3_I3', 'ingles-b1-U42', 13, 'key_word_transformation', 'Reescribe la frase usando la palabra clave ''launch'':', '{"prompt_es":"Reescribe la frase usando la palabra clave ''launch'':","mastery_tag":"grammar:b1-passive","explanation":"En la voz pasiva, la acción se centra en el producto y no en la empresa. El participio ''launched'' es clave aquí.","stimulus_en":"The company launched the new product. (KEY: launch)\n\nThe new product was launched... ...","correct_answer":"by the company.","example":"launch","complexity":2,"concept_tags":["grammar","passive","unit"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U42_B3_I4', 'ingles-b1-U42', 14, 'key_word_transformation', 'Reescribe la frase usando la palabra clave ''create'':', '{"prompt_es":"Reescribe la frase usando la palabra clave ''create'':","mastery_tag":"grammar:b1-passive","explanation":"Recuerda que en la voz pasiva, el foco está en la acción y el resultado. Se usa ''was'' y el participio ''created''.","stimulus_en":"The designer created the website. (KEY: create)\n\nThe website was created... ...","correct_answer":"by the designer.","example":"create","complexity":2,"concept_tags":["grammar","passive","unit"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U42_B3_I5', 'ingles-b1-U42', 15, 'key_word_transformation', 'Reescribe la frase usando la palabra clave ''conduct'':', '{"prompt_es":"Reescribe la frase usando la palabra clave ''conduct'':","mastery_tag":"grammar:b1-passive","explanation":"La voz pasiva se utiliza para señalar la acción sobre el experimento. Se utiliza ''was'' con el participio ''conducted''.","stimulus_en":"The researchers conducted the experiment. (KEY: conduct)\n\nThe experiment was conducted... ...","correct_answer":"by the researchers.","example":"conduct","complexity":2,"concept_tags":["grammar","passive","unit"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;

