INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U43_B1_I1', 'ingles-b1-U43', 1, 'multiple_choice', 'Choose the correct answer for each question about conditionals.', '{"prompt_es":"Choose the correct answer for each question about conditionals.","mastery_tag":"grammar:b1-conditionals","explanation":"Usamos ''will'' en la primera condicional para indicar un resultado futuro real.","stimulus_en":"If it rains tomorrow, I ___ my umbrella.","options":[{"id":"o1","text":"take"},{"id":"o2","text":"will take"},{"id":"o3","text":"took"},{"id":"o4","text":"would take"}],"correct_answer":"o2","complexity":2,"concept_tags":["grammar","conditionals","unit"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U43_B1_I2', 'ingles-b1-U43', 2, 'multiple_choice', 'Choose the correct answer for each question about conditionals.', '{"prompt_es":"Choose the correct answer for each question about conditionals.","mastery_tag":"grammar:b1-conditionals","explanation":"Usamos ''would'' en la segunda condicional para situaciones hipotéticas o irreales.","stimulus_en":"If I had more time, I ___ travel around the world.","options":[{"id":"o1","text":"will"},{"id":"o2","text":"would"},{"id":"o3","text":"can"},{"id":"o4","text":"might"}],"correct_answer":"o2","complexity":2,"concept_tags":["grammar","conditionals","unit"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U43_B1_I3', 'ingles-b1-U43', 3, 'multiple_choice', 'Choose the correct answer for each question about conditionals.', '{"prompt_es":"Choose the correct answer for each question about conditionals.","mastery_tag":"grammar:b1-conditionals","explanation":"En la primera condicional, usamos el presente simple ''tell'' para instrucciones futuras.","stimulus_en":"If you see John, ___ him to call me.","options":[{"id":"o1","text":"tell"},{"id":"o2","text":"told"},{"id":"o3","text":"will tell"},{"id":"o4","text":"would tell"}],"correct_answer":"o1","complexity":2,"concept_tags":["grammar","conditionals","unit"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U43_B1_I4', 'ingles-b1-U43', 4, 'multiple_choice', 'Choose the correct answer for each question about conditionals.', '{"prompt_es":"Choose the correct answer for each question about conditionals.","mastery_tag":"grammar:b1-conditionals","explanation":"En la segunda condicional, usamos ''would'' para situaciones que son poco probables.","stimulus_en":"If I won the lottery, I ___ buy a new house.","options":[{"id":"o1","text":"will"},{"id":"o2","text":"would"},{"id":"o3","text":"can"},{"id":"o4","text":"might"}],"correct_answer":"o2","complexity":2,"concept_tags":["grammar","conditionals","unit"]}')
