VALUES ('B2_U24_B3_I1', 'ingles-b2-U24', 7, 'multiple_choice', 'Choose the correct answer for each question about inversion in sentences.', '{"prompt_es":"Choose the correct answer for each question about inversion in sentences.","mastery_tag":"grammar:b2-inversion","explanation":"La inversión se utiliza para enfatizar ''rarely''. En inglés, después de un adverbio como ''rarely'', se usa el verbo auxiliar ''do''.","stimulus_en":"Rarely ___ we see such talent.","options":[{"id":"o1","text":"do"},{"id":"o2","text":"does"},{"id":"o3","text":"are"},{"id":"o4","text":"is"}],"correct_answer":"o1","complexity":3,"concept_tags":["grammar","inversion","unit"]}')
