INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B2_U1_B1_I1', 'ingles-b2-U1', 1, 'key_word_transformation', 'Reescribe la frase usando la palabra clave ''IF ONLY'':', '{"prompt_es":"Reescribe la frase usando la palabra clave ''IF ONLY'':","mastery_tag":"grammar:b2-wish-regret","explanation":"La frase expresa un deseo sobre el conocimiento actual que no se tiene.","stimulus_en":"I wish I knew more about marketing.\n\nIf only I... ...","correct_answer":"knew more about marketing.","example":"IF ONLY","complexity":3,"concept_tags":["grammar","wish","regret","lifestyle","routines"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B2_U1_B1_I2', 'ingles-b2-U1', 2, 'key_word_transformation', 'Reescribe la frase usando la palabra clave ''WISH'':', '{"prompt_es":"Reescribe la frase usando la palabra clave ''WISH'':","mastery_tag":"grammar:b2-wish-regret","explanation":"La frase indica un arrepentimiento sobre una acción pasada que no se realizó.","stimulus_en":"I regret not taking that job offer.\n\nI wish I... ...","correct_answer":"had taken that job offer.","example":"WISH","complexity":3,"concept_tags":["grammar","wish","regret","lifestyle","routines"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B2_U1_B1_I3', 'ingles-b2-U1', 3, 'key_word_transformation', 'Reescribe la frase usando la palabra clave ''IF ONLY'':', '{"prompt_es":"Reescribe la frase usando la palabra clave ''IF ONLY'':","mastery_tag":"grammar:b2-wish-regret","explanation":"Este deseo habla de una situación hipotética sobre la confianza en reuniones.","stimulus_en":"He wishes he were more confident in meetings.\n\nIf only he... ...","correct_answer":"were more confident in meetings.","example":"IF ONLY","complexity":3,"concept_tags":["grammar","wish","regret","lifestyle","routines"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B2_U1_B1_I4', 'ingles-b2-U1', 4, 'key_word_transformation', 'Reescribe la frase usando la palabra clave ''WISH'':', '{"prompt_es":"Reescribe la frase usando la palabra clave ''WISH'':","mastery_tag":"grammar:b2-wish-regret","explanation":"El arrepentimiento se expresa sobre una situación pasada no realizada.","stimulus_en":"She regrets missing the conference last year.\n\nShe wishes she... ...","correct_answer":"had attended the conference last year.","example":"WISH","complexity":3,"concept_tags":["grammar","wish","regret","lifestyle","routines"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B2_U1_B1_I5', 'ingles-b2-U1', 5, 'key_word_transformation', 'Reescribe la frase usando la palabra clave ''IF ONLY'':', '{"prompt_es":"Reescribe la frase usando la palabra clave ''IF ONLY'':","mastery_tag":"grammar:b2-wish-regret","explanation":"Esto expresa un deseo sobre la capacidad actual de gestionar el tiempo.","stimulus_en":"I wish I could manage my time better.\n\nIf only I... ...","correct_answer":"could manage my time better.","example":"IF ONLY","complexity":3,"concept_tags":["grammar","wish","regret","lifestyle","routines"]}')
