INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B2_U1_B2_I1', 'ingles-b2-U1', 6, 'multiple_choice_cloze', 'Fill in the blanks with the correct idiomatic expression from the options provided.', '{"prompt_es":"Fill in the blanks with the correct idiomatic expression from the options provided.","mastery_tag":"vocabulary:b2-idiomatic","main_text":"Understanding idiomatic expressions can greatly enhance your language skills. Here are some sentences with gaps to complete:\n\n1. He really gave me the [GAP 1] when I asked to borrow his car. (1)[GAP 2]\n\n2. Don''t worry about the presentation; just go with the [GAP 3]. (2)[GAP 4]\n\n3. She’s really [GAP 5] to her work and often stays late. (3)[GAP 6]\n\n4. After a long day, I like to kick back and [GAP 7]. (4)[GAP 8]\n\n5. They had a [GAP 9] of a time at the party last night! (5)[GAP 10]","gaps":[{"id":"1","options":[{"id":"o1","text":"green light"},{"id":"o2","text":"cold shoulder"},{"id":"o3","text":"red flag"},{"id":"o4","text":"blue sky"}]},{"id":"2","options":[{"id":"o1","text":"flow"},{"id":"o2","text":"line"},{"id":"o3","text":"road"},{"id":"o4","text":"way"}]},{"id":"3","options":[{"id":"o1","text":"devoted"},{"id":"o2","text":"dedicated"},{"id":"o3","text":"committed"},{"id":"o4","text":"attached"}]},{"id":"4","options":[{"id":"o1","text":"chill"},{"id":"o2","text":"sit"},{"id":"o3","text":"sleep"},{"id":"o4","text":"work"}]},{"id":"5","options":[{"id":"o1","text":"blast"},{"id":"o2","text":"breeze"},{"id":"o3","text":"storm"},{"id":"o4","text":"quake"}]}],"correct_answer":{"1":"o2","2":"o1","3":"o2","4":"o1","5":"o1"},"explanation":"(1) La expresión ''cold shoulder'' significa ignorar a alguien o no mostrar interés en algo. En este contexto, se refiere a que alguien no está dispuesto a prestar su coche.\n(2) La expresión ''go with the flow'' se refiere a aceptar las cosas como vienen y no resistirse a las circunstancias.\n(3) La palabra ''dedicated'' implica una fuerte devoción o compromiso hacia algo, en este caso, el trabajo.\n(4) La expresión ''kick back and chill'' significa relajarse y disfrutar del tiempo libre.\n(5) La expresión ''had a blast'' significa disfrutar mucho de una experiencia, en este caso, de la fiesta.","complexity":3,"concept_tags":["vocabulary","idiomatic","lifestyle","routines","grammar"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B2_U1_B3_I1', 'ingles-b2-U1', 7, 'multiple_choice', 'Choose the correct answer for each question about mixed conditionals.', '{"prompt_es":"Choose the correct answer for each question about mixed conditionals.","mastery_tag":"grammar:b2-mixed-conditionals","explanation":"La estructura correcta es ''would have + pasado participio'' para referirse a una condición pasada hipotética.","stimulus_en":"If she had studied harder, she ___ the exam successfully.","options":[{"id":"o1","text":"would pass"},{"id":"o2","text":"would have passed"},{"id":"o3","text":"will pass"},{"id":"o4","text":"passes"}],"correct_answer":"o2","complexity":3,"concept_tags":["grammar","mixed","conditionals","lifestyle","routines"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B2_U1_B3_I2', 'ingles-b2-U1', 8, 'multiple_choice', 'Choose the correct answer for each question about mixed conditionals.', '{"prompt_es":"Choose the correct answer for each question about mixed conditionals.","mastery_tag":"grammar:b2-mixed-conditionals","explanation":"Usamos ''would'' para hablar sobre situaciones futuras hipotéticas basadas en una condición presente.","stimulus_en":"If it rains tomorrow, we ___ the picnic in the park.","options":[{"id":"o1","text":"would cancel"},{"id":"o2","text":"cancel"},{"id":"o3","text":"canceled"},{"id":"o4","text":"would have canceled"}],"correct_answer":"o1","complexity":3,"concept_tags":["grammar","mixed","conditionals","lifestyle","routines"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B2_U1_B3_I3', 'ingles-b2-U1', 9, 'multiple_choice', 'Choose the correct answer for each question about mixed conditionals.', '{"prompt_es":"Choose the correct answer for each question about mixed conditionals.","mastery_tag":"grammar:b2-mixed-conditionals","explanation":"La forma correcta es ''would have'' para referirse a una situación pasada que no ocurrió.","stimulus_en":"If I were more confident, I ___ have asked for help last week.","options":[{"id":"o1","text":"would"},{"id":"o2","text":"will"},{"id":"o3","text":"would have"},{"id":"o4","text":"have"}],"correct_answer":"o3","complexity":3,"concept_tags":["grammar","mixed","conditionals","lifestyle","routines"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B2_U1_B3_I4', 'ingles-b2-U1', 10, 'multiple_choice', 'Choose the correct answer for each question about mixed conditionals.', '{"prompt_es":"Choose the correct answer for each question about mixed conditionals.","mastery_tag":"grammar:b2-mixed-conditionals","explanation":"''Would have caught'' se usa para expresar un resultado hipotético en el pasado que no ocurrió.","stimulus_en":"If they had left earlier, they ___ the train.","options":[{"id":"o1","text":"will catch"},{"id":"o2","text":"would have caught"},{"id":"o3","text":"would catch"},{"id":"o4","text":"caught"}],"correct_answer":"o2","complexity":3,"concept_tags":["grammar","mixed","conditionals","lifestyle","routines"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B2_U1_B3_I5', 'ingles-b2-U1', 11, 'multiple_choice', 'Choose the correct answer for each question about mixed conditionals.', '{"prompt_es":"Choose the correct answer for each question about mixed conditionals.","mastery_tag":"grammar:b2-mixed-conditionals","explanation":"La respuesta correcta es ''would tell'' para una situación presente basada en un conocimiento actual.","stimulus_en":"If I knew the answer, I ___ it to you right now.","options":[{"id":"o1","text":"will tell"},{"id":"o2","text":"would have told"},{"id":"o3","text":"would tell"},{"id":"o4","text":"would told"}],"correct_answer":"o3","complexity":3,"concept_tags":["grammar","mixed","conditionals","lifestyle","routines"]}')
