INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B2_U2_B1_I1', 'ingles-b2-U2', 1, 'fill_blanks', 'Complete the sentences with the correct words or phrases related to writing reviews and articles.', '{"prompt_es":"Complete the sentences with the correct words or phrases related to writing reviews and articles.","mastery_tag":"writing:b2-reviews-articles","main_text":"Writing a great review requires a clear structure and thoughtful opinions. First, it is important to (1)___ the subject matter effectively. You should provide enough background information to (2)___ your readers. Next, give your personal (3)___ on the issue, supported by examples. Finally, a good review should always include a (4)___ section, summarizing your main points and offering a (5)___ recommendation for the audience.","correct_answer":["analyze","inform","opinion","conclusion","final"],"explanation":"(1) Es importante entender y evaluar el tema para escribir una buena reseña.\n(2) Es esencial informar a los lectores sobre el tema para que puedan comprender la reseña.\n(3) Una opinión personal es crucial en una reseña, ya que aporta un punto de vista único.\n(4) Una conclusión ayuda a resumir lo que has escrito y a dejar una impresión final en el lector.\n(5) Una recomendación final es importante para guiar al lector sobre cómo proceder después de leer tu reseña.","complexity":3,"concept_tags":["writing","reviews","articles","unit"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B2_U2_B2_I1', 'ingles-b2-U2', 2, 'multiple_choice', 'What term describes the gradual process of social change in society?', '{"prompt_es":"What term describes the gradual process of social change in society?","mastery_tag":"vocabulary:b2-society","explanation":"La ''evolución'' se refiere al cambio gradual en la sociedad, a menudo a través de mejoras y adaptaciones a lo largo del tiempo.","stimulus_en":"","options":[{"id":"o1","text":"Evolution"},{"id":"o2","text":"Revolution"},{"id":"o3","text":"Tradition"},{"id":"o4","text":"Stagnation"}],"correct_answer":"o1","complexity":3,"concept_tags":["vocabulary","society","unit","grammar"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B2_U2_B2_I2', 'ingles-b2-U2', 3, 'multiple_choice', 'Which of the following is a common social issue regarding the economy?', '{"prompt_es":"Which of the following is a common social issue regarding the economy?","mastery_tag":"vocabulary:b2-society","explanation":"El ''desempleo'' es un problema social común que afecta la economía, ya que muchas personas no tienen trabajo.","stimulus_en":"","options":[{"id":"o1","text":"Unemployment"},{"id":"o2","text":"Celebration"},{"id":"o3","text":"Recreation"},{"id":"o4","text":"Satisfaction"}],"correct_answer":"o1","complexity":3,"concept_tags":["vocabulary","society","unit","grammar"]}')
