INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B2_U2_B2_I3', 'ingles-b2-U2', 4, 'multiple_choice', 'What is the primary role of a government in addressing social problems?', '{"prompt_es":"What is the primary role of a government in addressing social problems?","mastery_tag":"vocabulary:b2-society","explanation":"El papel principal del gobierno es ''regular las leyes'' para ayudar a resolver problemas sociales y garantizar el bienestar de la comunidad.","stimulus_en":"","options":[{"id":"o1","text":"To provide entertainment"},{"id":"o2","text":"To regulate laws"},{"id":"o3","text":"To increase taxes"},{"id":"o4","text":"To promote leisure"}],"correct_answer":"o2","complexity":3,"concept_tags":["vocabulary","society","unit","grammar"]}')
