INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B2_U2_B2_I4', 'ingles-b2-U2', 5, 'multiple_choice', 'Which term refers to a community''s shared beliefs and values?', '{"prompt_es":"Which term refers to a community''s shared beliefs and values?","mastery_tag":"vocabulary:b2-society","explanation":"La ''cultura'' de una comunidad incluye sus creencias y valores compartidos que definen su identidad.","stimulus_en":"","options":[{"id":"o1","text":"Tradition"},{"id":"o2","text":"Culture"},{"id":"o3","text":"Economy"},{"id":"o4","text":"Fashion"}],"correct_answer":"o2","complexity":3,"concept_tags":["vocabulary","society","unit","grammar"]}')
