VALUES ('B2_U2_B2_I5', 'ingles-b2-U2', 6, 'multiple_choice', 'What is often a result of social inequality?', '{"prompt_es":"What is often a result of social inequality?","mastery_tag":"vocabulary:b2-society","explanation":"El ''conflicto'' es a menudo una consecuencia de la desigualdad social, lo que lleva a tensiones y luchas dentro de la sociedad.","stimulus_en":"","options":[{"id":"o1","text":"Happiness"},{"id":"o2","text":"Conflict"},{"id":"o3","text":"Understanding"},{"id":"o4","text":"Unity"}],"correct_answer":"o2","complexity":3,"concept_tags":["vocabulary","society","unit","grammar"]}')
