INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B2_U2_B3_I1', 'ingles-b2-U2', 7, 'word_formation', 'Complete the sentences by forming the correct words from the base words provided.', '{"prompt_es":"Complete the sentences by forming the correct words from the base words provided.","mastery_tag":"vocabulary:b2-media","main_text":"The rise of digital technology has transformed the (1)___ of news. Traditional (2)___ are adapting to the new (3)___ of online platforms. In order to stay relevant, journalists must improve their (4)___ skills. Social media has changed the way we (5)___ information.","correct_answer":["COMMUNICATION","BROADCASTERS","FORMAT","WRITING","REPORTING"],"example":"COMMUNICATE, BROADCAST, FORMAT, WRITE, REPORT","explanation":"(1) undefined\n(2) undefined\n(3) undefined\n(4) undefined\n(5) undefined","complexity":3,"concept_tags":["vocabulary","media","unit"]}')
