VALUES ('B2_U3_B1_I1', 'ingles-b2-U3', 1, 'multiple_choice', 'Choose the best option to complete the sentences about formal writing.', '{"prompt_es":"Choose the best option to complete the sentences about formal writing.","mastery_tag":"writing:b2-formal-writing","explanation":"En cartas formales, el tono debe ser profesional para respetar la formalidad del contexto.","stimulus_en":"In formal letters, it is important to use a ____ tone.","options":[{"id":"o1","text":"friendly"},{"id":"o2","text":"casual"},{"id":"o3","text":"professional"},{"id":"o4","text":"informal"}],"correct_answer":"o3","complexity":3,"concept_tags":["writing","formal","unit","grammar"]}')
